import React, { Component } from 'react';
import AuthenticationRequest from '../../js/requests/AuthenticationRequest'
import CryptoJS from 'crypto-js'

class UserInfo extends Component {

    constructor(props) {
        super(props)

        this.state = {
            message : ''
        }

        this.handleChange = this.handleChange.bind(this)
        this.signinClicked = this.signinClicked.bind(this)
    }

    handleChange(event) {
        this.setState(
            {
                [event.target.name]
                    : event.target.value
            }
        )
    }

    updateClicked() {
        let cooky = window.localStorage;
        let user = {
            accessToken : cooky.getItem('userOathTokenType')+" "+ CryptoJS.AES.decrypt(cooky.getItem('accessTokenEncrypted'), 'somerandomsecretkey'),
            contentType: 'application/json'
        }
        AuthenticationRequest
            .getUserInfo(user)
            .then((response) => {
                console.log(response)
            })
    }

    render() {
        return (
            <div>
                <h1>Sign in</h1>
                <div className="container">
                    <button className="btn btn-success" onClick={this.updateClicked}>Sign in</button>
                </div>
            </div>
        )
    }
}

export default UserInfo
