import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import SigninPage from '../authen/LoginPage'
import SignupPage from '../authen/SignupPage'

class UserInfo extends Component {

    constructor(props) {
        super(props)

        this.state = {
            message : ''
        }

        this.handleChange = this.handleChange.bind(this)
        this.signinClicked = this.signinClicked.bind(this)
    }

    handleChange(event) {
        this.setState(
            {
                [event.target.name]
                    : event.target.value
            }
        )
    }

    updateClicked() {
        let cooky = window.localStorage;
        AuthenticationRequest
            .loginUser(user)
            .then((response) => {
                cooky.setItem('accessTokenEncrypted',CryptoJS.AES(response.data.accessToken,'somerandomsecretkey'));
                cooky.setItem('userOathTokenType', response.data.tokenType);
            })
    }

    render() {
        return (
            <div>
                <h1>Sign in</h1>
                <div className="container">
                    <button className="btn btn-success" onClick={this.updateClicked}>Sign in</button>
                </div>
            </div>
        )
    }
}

export default UserInfo
